section	{
  /* variable */
  --primary-font-family:"Roboto", sans-serif;
  --max-width:1400px;
  --primary-font-size:	18px;
  --primary-font-weight:	400;
  /* ---------------------- */

  display: flex;
  justify-content: space-between;
  align-items:	center;
  max-width: var(--max-width);
  font-family: var( --primary-font-family);	
  font-size:	var(--primary-font-size);
  line-height:	24px;
  font-weight: var( --primary-font-weight);
  margin:	0 auto;
  padding: 1.5rem 2rem;
  height:	90px;

	 nav	{
      display: flex;

  		button {
          display:	none;
          width:	90px;
      		height:	30px;
      		color: black;
      		background-color:	white;
      		border-radius:	5px;
        	margin-left:35px;
      }

    	a	{
        margin: 0 32px;
        border-bottom: 2px solid transparent;
        transition: border-color 0.1s;

        &:hover	{
          border-color: black;
          font-weight:	500;
          color:	#000000;
        }


    }

      svg	{
        margin: 0 42px;
       }

 }

	div	{

    .signUp	{
      width:	90px;
      height:	30px;
      color: white;
      background-color:	blue;
      border-radius:	5px;
    }

	.logIn	{
      width:	90px;
      height:	30px;
      color: black;
      background-color:	white;
      border-radius:	5px;
	}

  .close	{
    	display:	none;
  }

  .toggle-button	{
      position:	absolute;
      right:	20px;
      top:	0;
      width:	20px;
      height:	19px;
    	cursor: pointer;
      display:	none;
      flex-direction:	column;
      justify-content:	space-between;

    	span	{
          height:	4px;
          width:	100%;
          background-color:	black;
			}
		}
 }
}

/* menu screen */
@media(max-width:	793px)	{
  section	{
    flex-direction:	column;

  	nav	{
      flex-direction:	column;
      grid-gap:	10px;
      flex:	1;

        button {
          display:flex;
        }
        svg	{
          position:	absolute;
          top:	0;
          left:	0;
          margin:	5px 0;
    		}

  	}

    div	{
      position:	absolute;
      top:	0;
      right:	0;
      margin:	5px 0;

		.signUp	{
      position:	absolute;
      right:	70px;
      top:	0;

		}
  	.close	{
      display:	flex;
      margin:	0 32px;
      position:	absolute;
      right:	0;	
      top:	0;
  	}
    .logIn	{
      display:none;
    }

 		}


	}
}

/* mobile screen */
@media(max-width:400px)	{
  section	{
				nav	{
					.nav-links{
						display:	none;
						}

        button{
            display:none;
          	}

				}

				div	{
          .close	{
            display:	none;
            margin:	0;
						}

          .toggle-button	{
            display:flex;
          	}
				}
	}
}

