section{
  display: flex;
  justify-content: space-between;
  align-items:	center;
  max-width: 1400px;
  font-family:	"Roboto";
  font-size:	18px;
  line-height:	24px;
  font-weight:	400px;
  margin:	0 auto;
  padding: 1.5rem 2rem;

  nav	{
      display: flex;
    	a	{
        margin: 0 32px;
        border-bottom: 2px solid transparent;
        transition: border-color 0.1s;

        &:hover	{
          border-color: black;
          font-weight:	1000px;
          color:	#000000;
        }
    }

  svg	{
    	margin: 0 42px;
   }

 }

	div	{
    .signUp{
      width:	90px;
      height:	30px;
      color: white;
      background-color:	blue;
      border-radius:	5px;
    }

	.logIn{
      width:	90px;
      height:	30px;
      color: black;
      background-color:	white;
      border-radius:	5px;
	}
  .close	{
    	display:	none;
  }

  .toggle_button	{
    position:	absolute;
    right:	0;
    top:	0;
    width:	30px;
    height:	21px;
    display:	none;
    flex-direction:	column;
    justify-content:	space-between;
    span	{
      height:	3px;
      width:	100%;
      background-color:	black;

		}
	}
 }
}

@media(max-width:	500px)	{
  section	{
    flex-direction:	column;
    font-size:	12px;

  	nav	{
      flex-direction:	column;
      grid-gap:	10px;
      flex:	1;
  	}

    div	{
      position:	absolute;
      top:	0;
      right:	0;
      margin:	5px 0;

		.signUp	{
      position:	absolute;
      right:	70px;
      top:	0;
      width:	50px;
		}
  .close	{
      display:	flex;
      margin:	0 32px;
      position:	absolute;
      right:	0;	
      top:	0;
  }
  .logIn	{
    display:	none;
  }

 }
  svg	{
    position:	absolute;
    top:	0;
    left:	0;
    margin:	5px 0;
  }

	}
}

