section {
  /* variable */
  --primary-font-family: "Roboto", sans-serif;
  --max-width: 1400px;
  --primary-font-size: 18px;
  --primary-font-weight: 400;
  /* ---------------------- */

  display: flex;
  justify-content: space-between;
  align-items: center;
  max-width: var(--max-width);
  font-family: var(--primary-font-family);
  font-size: var(--primary-font-size);
  line-height: 24px;
  font-weight: var(--primary-font-weight);
  margin: 0 auto;
  padding: 1.5rem 2rem;

  nav {
    display: flex;
    gap: 32px;

    button {
      display: none;
      background-color: white;
      border-radius: 5px;
      align-self: center;
    }

    a {
      border-bottom: 1px solid transparent;
      transition: border-color 0.1s;
      align-self: center;

      &:hover {
        border-color: black;
        font-weight: 500;
        color: #000000;
      }
    }
  }

  div {
    display: flex;
    gap: 24px;
    .button {
      border-radius: 5px;
      padding: 10px;

      &.primary {
        color: white;
        background: blue;
      }
    }

    .close {
      display: none;
    }

    .toggle-button {
      position: absolute;
      right: 20px;
      top: 0;
      width: 20px;
      height: 19px;
      cursor: pointer;
      display: none;
      flex-direction: column;
      justify-content: space-between;

      span {
        height: 4px;
        width: 100%;
        background-color: black;
      }
    }
  }
}

/* menu screen */
@media (max-width: 793px) {
  section {
    flex-direction: column;
    nav {
      flex-direction: column;
      flex: 1;

      button {
        display: flex;
      }
      svg {
        position: absolute;
        top: 1;
        left: 0;
        margin: 5px 10px;
      }
    }

    div {
      position: absolute;
      top: 1;
      right: 0;
      margin: 5px 0;
      margin: 5px 10px;
      .button {
        &.primary {
          display: flex;
          position: absolute;
          right: 70px;
          top: 1;
          width:90px;
        }
      }
      .close {
        display: flex;
        margin: 0 32px;
        position: absolute;
        right: 0;
        top: 1;
      }
      .button {
        display: none;
      }
    }
  }
}

/* mobile screen */
@media (max-width: 400px) {
  section {
    nav {
      gap: 32px;
      .nav-links {
        display: none;
      }

      button {
        display: none;
      }
      svg {
        margin-top: 10px;
      }
    }

    div {
      .close {
        display: none;
        margin: 0;
      }
      .button {
        margin-top: 10px;
      }
      .toggle-button {
        display: flex;
        margin-top: 10px;
      }
    }
  }
}

section {
  font-size: var(--main-font-size);
  height-line: --main-height-line;
  max-width: var(--main-width);
  max-height: var(--main-height);
  padding: var(--main-padding);

  h5 {
    padding: var(--main-padding);
